class Saloimex{constructor(key){this.key=key?`${key.toLowerCase().split(" ").join("_")}_data`:"saloimex_data"}save(data){return localStorage.setItem(this.key,JSON.stringify(data)),data}load(){let data=!1;return localStorage.getItem(this.key)&&(data=JSON.parse(localStorage.getItem(this.key))),data}export(data){return this.save(data),btoa(JSON.stringify(data))}import(data){data=JSON.parse(atob(data));return this.save(data),data}}